package com.class8;

public class PractiseNestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int x=1; x<10; x++) {
			System.out.print(x+" ");
		
			for(int y=1; y<4; y++) {
				System.out.print(y+" ");
			}
			
			System.out.println();
		}

	}

}
